import React, { useState, useEffect } from "react";
import CurrentInfo from "./components/CurrentInfo";

function App() {
  let [city, setCity] = useState("College Park");
  let [cityInfo, setCityInfo] = useState({});
  let [isLoading, setLoading] = useState(true);

  const [apiData, setApiData] = useState({});
  const [getState, setGetState] = useState("tamilnadu");
  const [state, setState] = useState("tamilnadu");

  const apiKey = process.env.REACT_APP_API_KEY;
  const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${state}&appid=${apiKey}`;

  const inputHandler = (event) => {
    setGetState(event.target.value);
  };

  const submitHandler = () => {
    setState(getState);
  };

  const kelvinToFarenheit = (k) => {
    return (k - 273.15).toFixed(2);
  };

  // Side effect
  useEffect(() => {
    fetch(apiUrl)
      .then((res) => res.json())
      .then((data) => setApiData(data));
  }, [apiUrl]);

  function handleSubmit(event) {
    event.preventDefault();
    console.log("changeCity called");
    setCity(event.target.location.value);
    console.log("city is " + city);
  }

  useEffect(() => {
    // runs when city state changes
    async function fetchLocationData(location) {
      getInfo(location).then((response) => {
        console.log(response);
        setCityInfo(response);
      });
    }
    async function getInfo(name) {
      try {
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=${name}&APPID=94ed81b061343f42225830b57876c92c`,
          { mode: "cors" }
        );
        const location = await response.json();
        return location;
      } catch (err) {
        console.log(err);
        return err;
      }
    }
    console.log("user effect called");
    fetchLocationData(city);
    setLoading(false);
  }, [city]);

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  return (
    <div className="App">
      <header>
        <form action="" onSubmit={handleSubmit}>
          <input
            type="text"
            id="location"
            name="location"
            placeholder="Search City"
          ></input>
          <button type="submit">Submit</button>
        </form>
      </header>
      <main>
        {console.log("ab to render current info")}
        {console.log("Please....." + cityInfo)}
        <CurrentInfo city={cityInfo}></CurrentInfo>
      </main>
    </div>
  );
}

export default App;
