import React, { useState, useEffect } from "react";

function Forecast(props) {
  const [forecastData, setForecastData] = useState({});
  const apiKey = process.env.REACT_APP_API_KEY;

  const lat = props.geodata[0].lat;
  const lon = props.geodata[0].lat;

  function kelvinToFarenheit(k) {
    return ((k - 273.15) * (9 / 5) + 32).toFixed(2);
  }

  function kelvinToCelsius(k) {
    return (k - 273.15).toFixed(2);
  }

  useEffect(() => {
    (fetch(
        'api.openweathermap.org/data/2.5/forecast?lat=44.34&lon=10.99&appid=94ed81b061343f42225830b57876c92c'
      )
        .then((res) => res.json())
        .then((data) => console.log(data))).catch(e => {
            console.log(e);
        });
    console.log(forecastData);
  });



  return (
    <div className="forecast">{forecastData ? <div>
        <p>yes</p>
        {console.log(forecastData)}
    </div> : <p>Loading</p>}</div>
  );
}

export default Forecast;
