import React, { useState, useEffect } from "react";
import CurrentInfo from "./components/CurrentInfo";

function App() {
  const [apiData, setApiData] = useState({});
  const [getState, setGetState] = useState("College Park");
  const [location, setLocation] = useState("College Park");

  const apiKey = process.env.REACT_APP_API_KEY;
  const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${apiKey}`;

  // handle input change
  function inputHandler(event) {
    setGetState(event.target.value);
  }

  // handle submit button click
  function submitHandler(event) {
    event.preventDefault();
    setLocation(getState);
  }

  // useEffect runs when apiUrl changes
  useEffect(() => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${getState}&APPID=${apiKey}`
    )
      .then((res) => res.json())
      .then((data) => setApiData(data));
  }, [apiUrl]);

  return (
    <div className="App">
      <header className="">
        <div className="inner-header">
          <h1>React Weather App</h1>

          <form onSubmit={submitHandler}>
            <input
              type="text"
              id="location-name"
              onChange={inputHandler}
              value={getState}
              placeholder="Search City"
            />
            <button type="button" onClick={submitHandler}>
              Search
            </button>
          </form>
        </div>
      </header>

      {console.log(apiData)}
      <main>
        {(apiData.main && apiData.weather) ? (
          <CurrentInfo
            cityInfo={apiData.main}
            cityName={apiData.name}
            weather={apiData.weather}
          ></CurrentInfo>
        ) : (
          <p className="loading">Loading</p>
        )}
      </main>
    </div>
  );
}

export default App;
