import React, { useState, useEffect } from "react";
//import CurrentInfo from "./components/CurrentInfo";

function App() {

  const [apiData, setApiData] = useState({});
  const [getState, setGetState] = useState("College Park");
  const [location, setLocation] = useState("College Park");

  const apiKey = process.env.REACT_APP_API_KEY;
  const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${apiKey}`;

  function inputHandler(event) {
    setGetState(event.target.value);
  }

  function submitHandler() {
    setLocation(getState);
  }

  const kelvinToFarenheit = (k) => {
    return (k - 273.15).toFixed(2);
  };

  // useEffect runs when apiUrl changes
  useEffect(() => {
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${getState}&APPID=${apiKey}`)
      .then((res) => res.json())
      .then((data) => setApiData(data));    
  }, [apiUrl]);

  return (
    <div className="App">
      <header className="">
        <h1>React Weather App</h1>

        <div className="container">
        <div className="">
          <div className="col-auto">
            <label className="col-form-label">
              Enter Location :
            </label>
          </div>
          <div className="col-auto">
            <input
              type="text"
              id="location-name"
              className="form-control"
              onChange={inputHandler}
              value={getState}
            />
          </div>
          <button className="btn btn-primary mt-2" onClick={submitHandler}>
            Search
          </button>
        </div>
      </div>
      </header>



      <p>{apiData.name}</p>
    </div>
  );




}

export default App;
