import React, { useState, useEffect } from "react";
import CurrentInfo from "./components/CurrentInfo";

function App() {
  let [city, setCity] = useState("College Park");
  let [cityInfo, setCityInfo] = useState({});
  let [isLoading, setLoading] = useState(true);

  const [apiData, setApiData] = useState({});
  const [getState, setGetState] = useState("tamilnadu");
  const [state, setState] = useState("tamilnadu");

  const apiKey = process.env.REACT_APP_API_KEY;
  const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${state}&appid=${apiKey}`;

  const inputHandler = (event) => {
    setGetState(event.target.value);
  };

  const submitHandler = () => {
    setState(getState);
  };

  const kelvinToFarenheit = (k) => {
    return (k - 273.15).toFixed(2);
  };

  return (
    <div className="App">
      <header className="d-flex justify-content-center align-items-center">
        <h2>React Weather App</h2>
      </header>
      <div className="container">
        <div className="mt-3 d-flex flex-column justify-content-center align-items-center">
          <div class="col-auto">
            <label for="location-name" class="col-form-label">
              Enter Location :
            </label>
          </div>
          <div class="col-auto">
            <input
              type="text"
              id="location-name"
              class="form-control"
              onChange={inputHandler}
              value={getState}
            />
          </div>
          <button className="btn btn-primary mt-2" onClick={submitHandler}>
            Search
          </button>
        </div>

        <div className="card mt-3 mx-auto" style={{ width: '60vw' }}>
          {apiData.main ? (
            <div class="card-body text-center">
              <img
                src={`http://openweathermap.org/img/w/${apiData.weather[0].icon}.png`}
                alt="weather status icon"
                className="weather-icon"
              />

              <p className="h2">
                {kelvinToFarenheit(apiData.main.temp)}&deg; C
              </p>

              <p className="h5">
                <i className="fas fa-map-marker-alt"></i>{' '}
                <strong>{apiData.name}</strong>
              </p>

              <div className="row mt-4">
                <div className="col-md-6">
                  <p>
                    <i class="fas fa-temperature-low "></i>{' '}
                    <strong>
                      {kelvinToFarenheit(apiData.main.temp_min)}&deg; C
                    </strong>
                  </p>
                  <p>
                    <i className="fas fa-temperature-high"></i>{' '}
                    <strong>
                      {kelvinToFarenheit(apiData.main.temp_max)}&deg; C
                    </strong>
                  </p>
                </div>
                <div className="col-md-6">
                  <p>
                    {' '}
                    <strong>{apiData.weather[0].main}</strong>
                  </p>
                  <p>
                    <strong>
                      {' '}
                      {countries.getName(apiData.sys.country, 'en', {
                        select: 'official',
                      })}
                    </strong>
                  </p>
                </div>
              </div>
            </div>
          ) : (
            <h1>Loading</h1>
          )}
        </div>
      </div>
      <footer className="footer">
        <code>
          Created by{' '}
          <a href="https://github.com/imshines" target="none">
            imshines
          </a>{' '}
          using React
        </code>
      </footer>
    </div>
  );

  // Side effect
  useEffect(() => {
    fetch(apiUrl)
      .then((res) => res.json())
      .then((data) => setApiData(data));
  }, [apiUrl]);

  function handleSubmit(event) {
    event.preventDefault();
    console.log("changeCity called");
    setCity(event.target.location.value);
    console.log("city is " + city);
  }

  useEffect(() => {
    // runs when city state changes
    async function fetchLocationData(location) {
      getInfo(location).then((response) => {
        console.log(response);
        setCityInfo(response);
      });
    }
    async function getInfo(name) {
      try {
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=${name}&APPID=94ed81b061343f42225830b57876c92c`,
          { mode: "cors" }
        );
        const location = await response.json();
        return location;
      } catch (err) {
        console.log(err);
        return err;
      }
    }
    console.log("user effect called");
    fetchLocationData(city);
    setLoading(false);
  }, [city]);

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  return (
    <div className="App">
      <header>
        <form action="" onSubmit={handleSubmit}>
          <input
            type="text"
            id="location"
            name="location"
            placeholder="Search City"
          ></input>
          <button type="submit">Submit</button>
        </form>
      </header>
      <main>
        {console.log("ab to render current info")}
        {console.log("Please....." + cityInfo)}
        <CurrentInfo city={cityInfo}></CurrentInfo>
      </main>
    </div>
  );
}

export default App;
